sqlMap.select:
select SUPPLIER_ID, SUPPLIER_NAME, SUPPLIER_CONTACTNAME, SUPPLIER_ZIP, SUPPLIER_DESCRIPTION, SUPPLIER_FAX, SUPPLIER_MOBILE, SUPPLIER_TELEPHONE, SUPPLIER_ADDRESS, SUPPLIER_ACCESS, SUPPLIER_SCORE, CITY_ID, USER_ID, CREATED_BY, CREATED_DATE, LASTUPDATED_BY, LASTUPDATED_DATE, ACTIVE_FLAG, SUPPLIER_WEBSITE from t_da_supplier where SUPPLIER_ID in ( select DISTINCT(T_DA_SUPPLIER.SUPPLIER_ID) from
T_DA_SUPPLIER LEFT JOIN T_DA_SUPPLIERPRODUCT ON (T_DA_SUPPLIER.SUPPLIER_ID = T_DA_SUPPLIERPRODUCT.SUPPLIER_ID) LEFT JOIN T_DA_PRODUCTBRAND ON (T_DA_SUPPLIERPRODUCT.PRODUCTBRAND_ID = T_DA_PRODUCTBRAND.PRODUCTBRAND_ID) LEFT JOIN T_DA_PRODUCT ON (T_DA_PRODUCT.PRODUCT_ID = T_DA_PRODUCTBRAND.PRODUCT_ID) LEFT JOIN T_DA_CATEGORY ON (T_DA_CATEGORY.CATEGORY_ID = T_DA_PRODUCT.CATEGORY_ID) LEFT JOIN T_DA_BRAND ON (T_DA_PRODUCTBRAND.BRAND_ID = T_DA_BRAND.BRAND_ID) WHERE T_DA_SUPPLIER.SUPPLIER_NAME LIKE '%$value$%' OR T_DA_SUPPLIER.SUPPLIER_DESCRIPTION LIKE '%$value$%' OR T_DA_SUPPLIER.SUPPLIER_ADDRESS LIKE '%$value$%' OR T_DA_PRODUCT.PRODUCT_NAME LIKE '%$value$%' OR T_DA_CATEGORY.CATEGORY_NAME LIKE '%$value$%' OR T_DA_BRAND.BRAND_NAME LIKE '%$value$%' OR T_DA_BRAND.BRAND_ENGLISH LIKE '%$value$%'
)
